mapper.insert:
INSERT INTO CART_T(
	CID, USERID, PID, CARTQTY
) VALUES (
	#{cid}, #{userid}, #{pid}, #{cartqty}
)

mapper.select:
SELECT NVL(SUM(price * cartqty), 0) totalprice
FROM CART_T c, PRODUCT_T p
WHERE c.pid = p.pid AND c.userid = #{userid}

mapper.update:
UPDATE CART_T
SET CARTQTY = #{cartqty}
WHERE USERID = #{userid}
AND PID = #{pid}

mapper.select:
SELECT COUNT(*)
FROM CART_T
WHERE userid = #{userid}
AND pid = #{pid}

mapper.update:
UPDATE CART_T
SET cartqty = cartqty + #{cartqty}
WHERE userid = #{userid}
AND pid = #{pid}
